mapper.sql:
SELECT id
FROM _builds
WHERE name = #{jobConfigName}
  AND pipelineName = #{pipelineName}
  AND stageName = #{stageName}
  AND originalJobId IS NULL
ORDER BY id DESC
LIMIT #{count}

SELECT id
FROM _builds
WHERE name = #{jobConfigName}
  AND pipelineName = #{pipelineName}
  AND stageName = #{stageName}
  AND originalJobId IS NULL
  AND id < #{cursor}
ORDER BY id DESC
LIMIT #{count}

SELECT id
FROM (SELECT id
      FROM _builds
      WHERE name = #{jobConfigName}
        AND pipelineName = #{pipelineName}
        AND stageName = #{stageName}
        AND originalJobId IS NULL
        AND id > #{cursor}
      ORDER BY id ASC
      LIMIT #{count}) as JobInstanceIdsBeforeSpecifiedId
ORDER BY id DESC

mapper.select:
SELECT MAX(id) as latestRunId, MIN(id) as oldestRunId
FROM _builds
WHERE name = #{jobConfigName}
  AND pipelineName = #{pipelineName}
  AND stageName = #{stageName}
  AND originalJobId IS NULL
