mapper.update:
UPDATE stages
SET result = #{result}, completedByTransitionId = #{completedByTransitionId}, state = #{state}, cancelledBy = #{cancelledBy}
WHERE stages.id = #{stageId}

UPDATE stages
SET latestRun = false
WHERE pipelineId = #{pipelineId}
    AND name = #{stageName}

UPDATE stages
    SET artifactsDeleted = true
    WHERE id = #{stageId}

mapper.insert:
INSERT INTO stages (name, pipelineId, approvedBy, createdTime, orderId, approvalType, counter, state, latestRun, fetchMaterials, cleanWorkingDir, rerunOfCounter, configVersion, lastTransitionedTime)
VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)

    CALL IDENTITY();

mapper.select:
SELECT id as stageId, name as stageName, result as stageResult, counter as stageCounter, state as stageState, *
FROM _stages
WHERE name = #{stageName}
    AND pipelineName = #{pipelineName}
ORDER BY id DESC
LIMIT 1


WHERE completedByTransitionId IS NOT NULL
ORDER BY completedByTransitionId DESC
LIMIT #{pageLimit}


WHERE completedByTransitionId < #{value}
ORDER BY completedByTransitionId DESC
LIMIT #{pageLimit}


WHERE completedByTransitionId IS NOT NULL AND pipelines.name = #{pipelineName}
ORDER BY completedByTransitionId DESC
LIMIT #{pageLimit}


WHERE completedByTransitionId < #{value} AND pipelines.name = #{pipelineName}
ORDER BY completedByTransitionId DESC
LIMIT #{pageLimit}


WHERE stages.pipelineId = #{pipelineId}
AND

ORDER BY stageId DESC, builds.name, buildstatetransitions.statechangetime


WHERE pipelines.name = #{pipelineName}
AND pipelines.counter = #{pipelineCounter}
ORDER BY stageId DESC


WHERE pipelines.name = #{pipelineName}
AND pipelines.counter = #{pipelineCounter}
AND stages.name = #{stageName}
ORDER BY stages.counter

SELECT COUNT(*)
FROM _stages
WHERE id > #{stageId}
    AND name = #{stageName}
    AND pipelineName = #{pipelineName}

SELECT COUNT(1)
FROM _stages
WHERE name = #{stageName}
    AND pipelineName = #{pipelineName}

SELECT
    id as stageId, name as stageName, counter as stageCounter, state as stageState, *
FROM _stages
WHERE name = #{stageName}
AND pipelineName = #{pipelineName}
ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT
    id as stageId, name as stageName, counter as stageCounter, state as stageState, *
FROM _stages
WHERE
  name = #{stageName}
  AND pipelineName = #{pipelineName}
  AND id > #{id}
ORDER BY id
LIMIT #{limit}

SELECT MAX(_stages.id) as latestRunId, MIN(_stages.id) as oldestRunId
FROM _stages
WHERE pipelineName = #{pipelineName}
  AND name = #{stageName}

mapper.sql:
SELECT id
FROM _stages
WHERE name = #{stageName}
  AND pipelineName = #{pipelineName}
ORDER BY id DESC
LIMIT #{limit}

SELECT id
FROM _stages
WHERE name = #{stageName}
  AND pipelineName = #{pipelineName}
  AND _stages.id < #{cursor}
ORDER BY id DESC
LIMIT #{limit}

SELECT id
FROM (SELECT id
      FROM _stages
      WHERE name = #{stageName}
        AND pipelineName = #{pipelineName}
        AND _stages.id > #{cursor}
      ORDER BY id ASC
      LIMIT #{limit}) as StageInstanceIdsBeforeSpecifiedId
ORDER BY id DESC

mapper.select:
SELECT count(1)
FROM stages
JOIN pipelines ON pipelines.id = stages.pipelineId
WHERE pipelines.name = #{pipelineName}
AND stages.name = #{stageName}
AND stages.completedByTransitionId IS NULL

SELECT Max(stages.counter) FROM stages
JOIN pipelines ON pipelines.id = stages.pipelineId
AND pipelines.name= #{pipelineName} AND pipelines.counter = #{pipelineCounter}
WHERE stages.name= #{stageName}

SELECT builds.*, builds.id as buildId, stages.name as stageName, stages.counter as stageCounter, stages.pipelineId as pipelineId, stages.approvedBy, stages.cancelledBy, stages.createdTime, stages.orderId, stages.result as stageResult, stages.completedByTransitionId as completedByTransitionId, stages.state as stageState, stages.approvalType, stages.latestRun, stages.fetchMaterials, stages.cleanWorkingDir, stages.rerunOfCounter, stages.artifactsDeleted, stages.configVersion, stages.lastTransitionedTime, pipelines.name as pipelineName, pipelines.counter as pipelineCounter, pipelines.label as pipelineLabel, buildstatetransitions.currentState, buildstatetransitions.statechangetime, buildstatetransitions.id as stateId FROM stages JOIN pipelines ON pipelines.id = stages.pipelineId AND pipelines.name = #{pipelineName} AND pipelines.label = #{pipelineLabel}
AND pipelines.counter = #{pipelineCounter}
JOIN builds ON stages.id = builds.stageId AND builds.ignored != true LEFT JOIN buildstatetransitions ON builds.id = buildstatetransitions.buildId WHERE stages.name = #{stageName} AND stages.counter = #{stageCounter} ORDER BY builds.name, buildstatetransitions.id
